import React, { useState } from 'react';
import { DiagnosisResult } from '../types';

interface ResultPageProps {
  result: DiagnosisResult;
  onViewProducts: () => void;
  onRestart: () => void;
}

const ResultPage: React.FC<ResultPageProps> = ({
  result,
  onViewProducts,
  onRestart
}) => {
  const [showColumnDetail, setShowColumnDetail] = useState(false);

  const getAgeGroupDisplay = (ageGroup: string) => {
    switch (ageGroup) {
      case 'child': return '小児（3〜12歳）';
      case 'teen': return '中高生（13〜18歳）';
      case 'adult': return '成人（19〜49歳）';
      case 'middle': return '中高年（50〜64歳）';
      case 'senior': return '高齢者（65歳以上）';
      default: return '';
    }
  };

  return (
    <div className="content">
      <div className="result-section">
        <div className="character">
          🦷
        </div>

        <h2 className="result-title">{result.title}</h2>

        <div className="age-group-display">
          {getAgeGroupDisplay(result.ageGroup)}の診断結果
        </div>

        <p className="result-description">{result.description}</p>

        {/* 口腔内画像表示 */}
        {result.oralImage && (
          <div className="oral-image-section">
            <h3>症状の特徴</h3>
            <div className="oral-image-container">
              <img 
                src={result.oralImage} 
                alt="口腔内の状態" 
                className="oral-image"
                onError={(e) => {
                  // 画像が読み込めない場合のフォールバック
                  e.currentTarget.style.display = 'none';
                }}
              />
              <div className="oral-image-placeholder">
                🦷 口腔内の状態イメージ
              </div>
            </div>
          </div>
        )}

        {/* リスクの説明 */}
        {result.riskDescription && (
          <div className="risk-description">
            <h3>なぜこの状態になるのか</h3>
            <p>{result.riskDescription}</p>
          </div>
        )}

        <div className="advice-list">
          <h3>おすすめのケア方法</h3>
          <ul>
            {result.advice.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>

        {/* 院内コラム情報 */}
        {result.clinicColumn && (
          <div className="clinic-column">
            <h3>この年代に多いお悩み</h3>
            <div className="column-preview">
              <div className="column-icon">📋</div>
              <div className="column-content">
                <h4>{result.clinicColumn.title}</h4>
                <p className="column-brief">{result.clinicColumn.description}</p>
              </div>
            </div>
            <button 
              className="column-detail-button"
              onClick={() => setShowColumnDetail(true)}
            >
              詳しく見る
            </button>
          </div>
        )}
      </div>

      <div className="navigation">
        <button 
          className="nav-button primary" 
          onClick={onViewProducts}
        >
          おすすめケアグッズを見る
        </button>
        <button 
          className="nav-button" 
          onClick={onRestart}
        >
          もう一度診断する
        </button>
      </div>

      {/* 院内コラム詳細モーダル */}
      {showColumnDetail && result.clinicColumn && (
        <div className="modal-overlay" onClick={() => setShowColumnDetail(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{result.clinicColumn.title}</h3>
              <button 
                className="modal-close"
                onClick={() => setShowColumnDetail(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <p>{result.clinicColumn.description}</p>
              <div className="column-features">
                <h4>当院での取り組み</h4>
                <ul>
                  <li>専門的な検査と診断</li>
                  <li>年齢に応じたケア指導</li>
                  <li>定期的なフォローアップ</li>
                  <li>予防プログラムのご提案</li>
                </ul>
              </div>
              <div className="contact-info">
                <p>詳しくはスタッフまでお気軽にお声かけください。</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ResultPage;
