        {/* Èô¢ÂÜÖ„Ç≥„É©„É†ÊÉÖÂ†± */}
        {result.clinicColumn && (
          <div className="clinic-column">
            <h3>„Åì„ÅÆÂπ¥‰ª£„Å´Â§ö„ÅÑ„ÅäÊÇ©„Åø</h3>
            <div className="column-preview">
              <div className="column-icon">üìã</div>
              <div className="column-content">
                <h4>{result.clinicColumn.title}</h4>
                <p className="column-brief">{result.clinicColumn.description}</p>
              </div>
            </div>
            <button 
              className="column-detail-button"
              onClick={() => setShowColumnDetail(true)}
            >
              Ë©≥„Åó„ÅèË¶ã„Çã
            </button>
          </div>
        )}
      </div>

      <div className="navigation">
        <button 
          className="nav-button primary" 
          onClick={onViewProducts}
        >
          „Åä„Åô„Åô„ÇÅ„Ç±„Ç¢„Ç∞„ÉÉ„Ç∫„ÇíË¶ã„Çã
        </button>
        <button 
          className="nav-button" 
          onClick={onRestart}
        >
          „ÇÇ„ÅÜ‰∏ÄÂ∫¶Ë®∫Êñ≠„Åô„Çã
        </button>
      </div>

      {/* Èô¢ÂÜÖ„Ç≥„É©„É†Ë©≥Á¥∞„É¢„Éº„ÉÄ„É´ */}
      {showColumnDetail && result.clinicColumn && (
        <div className="modal-overlay" onClick={() => setShowColumnDetail(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
