import React, { useState } from 'react';
import { CareProduct } from '../types';

interface ProductsPageProps {
  products: CareProduct[];
  onBack: () => void;
  onRestart: () => void;
}

const ProductsPage: React.FC<ProductsPageProps> = ({
  products,
  onBack,
  onRestart
}) => {
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const categories = {
    all: '„Åô„Åπ„Å¶',
    toothbrush: 'Ê≠Ø„Éñ„É©„Ç∑',
    toothpaste: 'Ê≠ØÁ£®„ÅçÂâ§',
    interdental: 'Ê≠ØÈñìÊ∏ÖÊéÉÂÖ∑',
    mouthwash: '„ÅÜ„Åå„ÅÑ„Éª‰øùÊπø',
    other: '„Åù„ÅÆ‰ªñ'
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'toothbrush': return 'ü™•';
      case 'toothpaste': return 'üß¥';
      case 'interdental': return 'üßµ';
      case 'mouthwash': return 'üíß';
      case 'other': return 'üîß';
      default: return 'üì¶';
    }
  };

  const getProductIcon = (category: string, productName: string) => {
    switch (category) {
      case 'toothbrush':
        if (productName.includes('‰π≥ÂπºÂÖê') || productName.includes('„Åì„Å©„ÇÇ')) return 'üë∂ü™•';
        if (productName.includes('„Çø„Éï„Éà')) return 'ü™•';
        if (productName.includes('„ÇÑ„Çè„Çâ„Åã')) return '‚ú®ü™•';
        return 'ü™•';
      case 'toothpaste':
        if (productName.includes('„ÉÅ„Çß„ÉÉ„ÇØ„Ç¢„ÉÉ„Éó')) return '‚úÖüß¥';
        if (productName.includes('„Éõ„ÉØ„Ç§„Éà')) return '‚≠êüß¥';
        if (productName.includes('„É™„Éö„É™„Ç™')) return 'üåøüß¥';
        if (productName.includes('„Çª„É≥„Ç∑„ÉÜ„Ç£„Éñ')) return 'üíôüß¥';
        return 'üß¥';
      case 'interdental':
        if (productName.includes('„Éï„É≠„Çπ')) return 'üßµ';
        if (productName.includes('Ê≠ØÈñì„Éñ„É©„Ç∑')) return 'üî∏';
        return 'üßµ';
      case 'mouthwash':
        if (productName.includes('„Éô„Éì„Éº')) return 'üçºüíß';
        if (productName.includes('„Ç™„Éº„É©„É´„Éî„Éº„Çπ')) return 'üåøüíß';
        if (productName.includes('„Ç≥„É≥„ÇØ„Éº„É´')) return 'üõ°Ô∏èüíß';
        return 'üíß';
      case 'other':
        if (productName.includes('„Ç¨„Éº„Çº')) return 'üßΩüë∂';
        if (productName.includes('ÂÖ•„ÇåÊ≠Ø')) return 'ü¶∑üßΩ';
        if (productName.includes('Ëàå')) return 'üëÖüßΩ';
        return 'üîß';
      default:
        return 'üì¶';
    }
  };

  const filteredProducts = selectedCategory === 'all' 
    ? products 
    : products.filter(product => product.category === selectedCategory);

  const groupedProducts = Object.entries(categories).reduce((acc, [key, label]) => {
    if (key === 'all') return acc;

    const categoryProducts = products.filter(product => product.category === key);
    if (categoryProducts.length > 0) {
      acc[key] = {
        label,
        products: categoryProducts
      };
    }
    return acc;
  }, {} as Record<string, { label: string; products: CareProduct[] }>);

  return (
    <div className="content">
      <div className="products-section">
        <h2 className="products-title">„ÅÇ„Å™„Åü„Å´„Åä„Åô„Åô„ÇÅ„ÅÆ„Ç±„Ç¢„Ç∞„ÉÉ„Ç∫</h2>

        <p className="products-description">
          Ë®∫Êñ≠ÁµêÊûú„Å´Âü∫„Å•„ÅÑ„Å¶„ÄÅ„ÅÇ„Å™„Åü„ÅÆÂè£ËÖîÁä∂ÊÖã„Å´ÈÅ©„Åó„Åü„Ç±„Ç¢„Ç∞„ÉÉ„Ç∫„Çí„ÅîÁ¥π‰ªã„Åó„Åæ„Åô„ÄÇ
          Èô¢ÂÜÖ„Åß„ÅäÊ±Ç„ÇÅ„ÅÑ„Åü„Å†„Åë„Åæ„Åô„ÄÇ
        </p>

        {/* „Ç´„ÉÜ„Ç¥„É™„Éï„Ç£„É´„Çø„Éº */}
        <div className="category-filter">
          {Object.entries(categories).map(([key, label]) => (
            <button
              key={key}
              className={`category-button ${selectedCategory === key ? 'active' : ''}`}
              onClick={() => setSelectedCategory(key)}
            >
              {getCategoryIcon(key)} {label}
            </button>
          ))}
        </div>

        {/* ÂïÜÂìÅ‰∏ÄË¶ßÔºà„Éï„Ç£„É´„Çø„ÉºË°®Á§∫Ôºâ */}
        {selectedCategory === 'all' ? (
          // „Ç´„ÉÜ„Ç¥„É™Âà•„Å´Êï¥ÁêÜ„Åó„Å¶Ë°®Á§∫
          <div className="products-by-category">
            {Object.entries(groupedProducts).map(([categoryKey, categoryData]) => (
              <div key={categoryKey} className="category-section">
                <h3 className="category-title">
                  {getCategoryIcon(categoryKey)} {categoryData.label}
                </h3>
                <div className="products-grid">
                  {categoryData.products.map((product) => (
                    <div key={product.id} className="product-card">
                      <div className="product-image">
                        {/* ÂÆüÈöõ„ÅÆÂïÜÂìÅÁîªÂÉè„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅØË°®Á§∫ */}
                        {product.image ? (
                          <>
                            <img 
                              src={product.image} 
                              alt={product.name}
                              className="product-img"
                              onError={(e) => {
                                // ÁîªÂÉè„ÅåË™≠„ÅøËæº„ÇÅ„Å™„ÅÑÂ†¥Âêà„ÅØ„Ç¢„Ç§„Ç≥„É≥Ë°®Á§∫„Å´„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ
                                const target = e.target as HTMLImageElement;
                                target.style.display = 'none';
                                const placeholder = target.nextElementSibling as HTMLElement;
                                if (placeholder) {
                                  placeholder.style.display = 'flex';
                                }
                              }}
                            />
                            <div className="product-image-placeholder" style={{ display: 'none' }}>
                              {getProductIcon(product.category, product.name)}
                            </div>
                          </>
                        ) : (
                          // ÁîªÂÉè„Åå„Å™„ÅÑÂ†¥Âêà„ÅØ„Ç¢„Ç§„Ç≥„É≥Ë°®Á§∫
                          <div className="product-image-placeholder">
                            {getProductIcon(product.category, product.name)}
                          </div>
                        )}
                      </div>
                      <div className="product-info">
                        <h4 className="product-name">{product.name}</h4>
                        <p className="product-description">{product.description}</p>
                        {product.price && (
                          <div className="product-price">¬•{product.price.toLocaleString()}</div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ) : (
          // ÈÅ∏Êäû„Åó„Åü„Ç´„ÉÜ„Ç¥„É™„ÅÆÂïÜÂìÅ„ÅÆ„ÅøË°®Á§∫
          <div className="products-grid">
            {filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-image">
                  {/* ÂÆüÈöõ„ÅÆÂïÜÂìÅÁîªÂÉè„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅØË°®Á§∫ */}
                  {product.image ? (
                    <>
                      <img 
                        src={product.image} 
                        alt={product.name}
                        className="product-img"
                        onError={(e) => {
                          // ÁîªÂÉè„ÅåË™≠„ÅøËæº„ÇÅ„Å™„ÅÑÂ†¥Âêà„ÅØ„Ç¢„Ç§„Ç≥„É≥Ë°®Á§∫„Å´„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ
                          const target = e.target as HTMLImageElement;
                          target.style.display = 'none';
                          const placeholder = target.nextElementSibling as HTMLElement;
                          if (placeholder) {
                            placeholder.style.display = 'flex';
                          }
                        }}
                      />
                      <div className="product-image-placeholder" style={{ display: 'none' }}>
                        {getProductIcon(product.category, product.name)}
                      </div>
                    </>
                  ) : (
                    // ÁîªÂÉè„Åå„Å™„ÅÑÂ†¥Âêà„ÅØ„Ç¢„Ç§„Ç≥„É≥Ë°®Á§∫
                    <div className="product-image-placeholder">
                      {getProductIcon(product.category, product.name)}
                    </div>
                  )}
                </div>
                <div className="product-info">
                  <h4 className="product-name">{product.name}</h4>
                  <p className="product-description">{product.description}</p>
                  {product.price && (
                    <div className="product-price">¬•{product.price.toLocaleString()}</div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {filteredProducts.length === 0 && (
          <div className="no-products">
            „Åì„ÅÆ„Ç´„ÉÜ„Ç¥„É™„Å´„ÅØË©≤ÂΩì„Åô„ÇãÂïÜÂìÅ„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ
          </div>
        )}

        <div className="products-note">
          <h4>„ÅîË≥ºÂÖ•„Å´„Å§„ÅÑ„Å¶</h4>
          <p>
            „Åì„Çå„Çâ„ÅÆÂïÜÂìÅ„ÅØÈô¢ÂÜÖ„Åß„ÅäÊ±Ç„ÇÅ„ÅÑ„Åü„Å†„Åë„Åæ„Åô„ÄÇ<br />
            Ë©≥„Åó„ÅÑ‰ΩøÁî®ÊñπÊ≥ï„ÇÑÈÅ∏„Å≥Êñπ„Å´„Å§„ÅÑ„Å¶„ÄÅ„ÅäÊ∞óËªΩ„Å´„Çπ„Çø„ÉÉ„Éï„Åæ„Åß„ÅîÁõ∏Ë´á„Åè„Å†„Åï„ÅÑ„ÄÇ
          </p>
        </div>
      </div>

      <div className="navigation">
        <button 
          className="nav-button" 
          onClick={onBack}
        >
          Ë®∫Êñ≠ÁµêÊûú„Å´Êàª„Çã
        </button>
        <button 
          className="nav-button primary" 
          onClick={onRestart}
        >
          „ÇÇ„ÅÜ‰∏ÄÂ∫¶Ë®∫Êñ≠„Åô„Çã
        </button>
      </div>
    </div>
  );
};

export default ProductsPage;
