import React, { useState, useRef, useEffect, useCallback } from 'react';
import { Question, UserAnswers } from '../types';

interface QuestionsPageProps {
  userAnswers: UserAnswers;
  currentQuestionIndex: number;
  questions: Question[];
  onAnswer: (questionId: string, answer: boolean) => void;
}

const QuestionsPage: React.FC<QuestionsPageProps> = ({
  currentQuestionIndex,
  questions,
  onAnswer
}) => {
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [startX, setStartX] = useState(0);
  const cardRef = useRef<HTMLDivElement>(null);

  // „Éû„Ç¶„Çπ/„Çø„ÉÉ„ÉÅÊìç‰Ωú‰∏≠
  const handleMove = useCallback((clientX: number) => {
    if (!isDragging) return;

    const offset = clientX - startX;
    setDragOffset(offset);
  }, [isDragging, startX]);

  // „Éû„Ç¶„Çπ/„Çø„ÉÉ„ÉÅÊìç‰ΩúÁµÇ‰∫Ü
  const handleEnd = useCallback(() => {
    if (!isDragging) return;

    setIsDragging(false);

    const threshold = 100; // „Çπ„ÉØ„Ç§„ÉóÂà§ÂÆö„ÅÆÈñæÂÄ§

    if (Math.abs(dragOffset) > threshold) {
      if (dragOffset > 0) {
        // Âè≥„Çπ„ÉØ„Ç§„Éó = „ÅÑ„ÅÑ„Åà
        if (questions[currentQuestionIndex]) {
          onAnswer(questions[currentQuestionIndex].id, false);
        }
      } else {
        // Â∑¶„Çπ„ÉØ„Ç§„Éó = „ÅØ„ÅÑ
        if (questions[currentQuestionIndex]) {
          onAnswer(questions[currentQuestionIndex].id, true);
        }
      }
    }

    setDragOffset(0);
  }, [isDragging, dragOffset, questions, currentQuestionIndex, onAnswer]);

  // „Ç∞„É≠„Éº„Éê„É´„Éû„Ç¶„Çπ„Ç§„Éô„É≥„Éà„ÅÆË®≠ÂÆö
  useEffect(() => {
    if (isDragging) {
      const handleGlobalMouseMove = (e: MouseEvent) => {
        handleMove(e.clientX);
      };

      const handleGlobalMouseUp = (e: MouseEvent) => {
        handleEnd();
      };

      document.addEventListener('mousemove', handleGlobalMouseMove);
      document.addEventListener('mouseup', handleGlobalMouseUp);

      return () => {
        document.removeEventListener('mousemove', handleGlobalMouseMove);
        document.removeEventListener('mouseup', handleGlobalMouseUp);
      };
    }
  }, [isDragging, handleMove, handleEnd]);

  if (questions.length === 0) {
    return <div className="content">Ë≥™Âïè„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì</div>;
  }

  const currentQuestion = questions[currentQuestionIndex];
  const progress = ((currentQuestionIndex + 1) / questions.length) * 100;

  // „Éû„Ç¶„Çπ/„Çø„ÉÉ„ÉÅÊìç‰ΩúÈñãÂßã
  const handleStart = (clientX: number) => {
    setIsDragging(true);
    setStartX(clientX);
    setDragOffset(0);
  };

  // „Éû„Ç¶„Çπ„Ç§„Éô„É≥„Éà
  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    handleStart(e.clientX);
  };

  // „Çø„ÉÉ„ÉÅ„Ç§„Éô„É≥„Éà
  const handleTouchStart = (e: React.TouchEvent) => {
    handleStart(e.touches[0].clientX);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    handleMove(e.touches[0].clientX);
  };

  const handleTouchEnd = (e: React.TouchEvent) => {
    handleEnd();
  };

  // „Ç´„Éº„Éâ„ÅÆÂ§âÂΩ¢Ë®àÁÆó
  const getCardStyle = () => {
    const rotation = dragOffset * 0.1; // ÂõûËª¢ËßíÂ∫¶
    const opacity = Math.max(0.7, 1 - Math.abs(dragOffset) * 0.002);

    return {
      transform: `translateX(${dragOffset}px) rotate(${rotation}deg)`,
      opacity,
      transition: isDragging ? 'none' : 'all 0.3s ease-out'
    };
  };

  // „Çπ„ÉØ„Ç§„ÉóÊñπÂêë„ÅÆË°®Á§∫
  const getSwipeIndicator = () => {
    if (Math.abs(dragOffset) < 50) return null;

    if (dragOffset > 0) {
      return <div className="swipe-indicator no">„ÅÑ„ÅÑ„Åà</div>;
    } else {
      return <div className="swipe-indicator yes">„ÅØ„ÅÑ</div>;
    }
  };

  return (
    <div className="content">
      <div className="question-swipe-container">
        <div className="progress">
          Ë≥™Âïè {currentQuestionIndex + 1} / {questions.length}
        </div>
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${progress}%` }}
          />
        </div>

        <div className="answer-instructions">
          <h3>üìù ÂõûÁ≠îÊñπÊ≥ï</h3>
          <div className="instruction-methods">
            <div className="method-item">
              <span className="method-icon">üëÜ</span>
              <span className="method-text">„Çπ„ÉØ„Ç§„Éó„ÅßÂõûÁ≠î</span>
            </div>
          </div>
        </div>

        <div className="swipe-instructions">
          <div className="instruction-item">
            <span className="swipe-arrow left">‚Üê</span>
            <span>„ÅØ„ÅÑ</span>
          </div>
          <div className="instruction-item">
            <span>„ÅÑ„ÅÑ„Åà</span>
            <span className="swipe-arrow right">‚Üí</span>
          </div>
        </div>

        <div className="question-card-stack">
          <div 
            ref={cardRef}
            className="question-card swipeable"
            style={getCardStyle()}
            onMouseDown={handleMouseDown}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            <div className="question-text">
              {currentQuestion.text}
            </div>

            {getSwipeIndicator()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuestionsPage;
